void printf_init(int q[9][9])
{
	for (int i = 0; i <= 18; i++)
	{
		for (int j = 0; j <= 18; j++)
		{
			F_[i][j] = -1;
			F[i][j] = F_O[i][j];
		}
	}
	for (int i = 0; i <= 8; i++)
	{
		for (int j = 0; j <= 8; j++)
		{
			q_f[i][j] = 0;
			F[i * 2 + 1][j * 2 + 1] = q[i][j];
			if (q[i][j] != 0)
			{
				q_f[i][j] = 1;
			}
		}
	}
	clean();
	form();

}

void clean(void)
{
	for (int i = 1; i <= 30; i++)
	{
		gotoxy(0, i);
		for (int j = 1; j <= 59; j++)
			wprintf(L"%s", L"  ");
	}

}
void printf_screen(int q_n[9][9], int cur[2], long ms_)
{

	CCI.bVisible = FALSE; // 是否可視
	CCI.dwSize = 15; // 設定大小,1~100
	SetConsoleCursorInfo(GetStdHandle(STD_OUTPUT_HANDLE), &CCI);
	gotoxy(36 * 2 - 1, 9);
	wprintf(L"%s", L"　　　↑                 ");
	gotoxy(36 * 2 - 1, 10);
	wprintf(L"%s", L"　←　↓　→：移動游標    ");
	gotoxy(36 * 2 - 1, 12);
	wprintf(L"%s", L"　ｓｐａｃｅ：清除格子   ");
	gotoxy(36 * 2 - 1, 14);
	wprintf(L"%s", L"　１　～　９：:輸入數字 ");
	gotoxy(36 * 2 - 1, 16);
	wprintf(L"%s", L"ＣＴＲＬ＋Ｑ：回到主畫面 ");
	gotoxy(36 * 2 - 1, 18);
	for (int i = 0; i < 9; i++)
	{
		for (int j = 0; j < 9; j++)
		{
			if (q_f[i][j] == 0)
				F[i * 2 + 1][j * 2 + 1] = q_n[i][j];
		}
	}

	form();
	CCI.bVisible = TRUE; // 是否可視
	CCI.dwSize = 15; // 設定大小,1~100
	SetConsoleCursorInfo(GetStdHandle(STD_OUTPUT_HANDLE), &CCI);
	gotoxy(16 * 2 - 1 + cur[0] * 4, 7 + cur[1] * 2);//goto(x y) array (y x)


}
void printf_record(int times[5], long ave_time[5], long fas_time[5])
{
	clean();
	gotoxy(26 * 2 - 1, 5);
	wprintf(L"%s", L"統　　　　　　　　　計");
	for (int i = 0; i < 5; i++)
	{
		gotoxy(14 * 2 - 1 + 5, 9 + 3 * i);
		wprintf(L"難度　%d　次數 %d%d%d　平均時間 %ld%ld：%ld%ld：%ld%ld　最快時間 %ld%ld：%ld%ld：%ld%ld",
			i + 1, times[i] / 100, times[i] / 10 % 10, times[i] % 10,
			ave_time[i] / 1000 / 60 / 60 / 10, ave_time[i] / 1000 / 60 / 60 % 10,
			ave_time[i] / 1000 / 60 % 60 / 10, ave_time[i] / 1000 / 60 % 60 % 10,
			ave_time[i] / 1000 % 60 / 10, ave_time[i] / 1000 % 60 % 10,
			fas_time[i] / 1000 / 60 / 60 / 10, fas_time[i] / 1000 / 60 / 60 % 10,
			fas_time[i] / 1000 / 60 % 60 / 10, fas_time[i] / 1000 / 60 % 60 % 10,
			fas_time[i] / 1000 % 60 / 10, fas_time[i] / 1000 % 60 % 10);
	}
	gotoxy(28 * 2 - 1, 25);
	wprintf(L"%s", L"案任意見返回");
}

